mapper.update:
UPDATE t_config
SET DRVENID = #{drVenId}
    , DRREGISTRATIONID = #{drRegistrationId}
WHERE DRBUSINESSID = #{drBusinessId}

mapper.insert:
INSERT INTO tb_dr_issue_api (
                             reg_dt
                           , reg_id
                           , issue_date
                           , issue_hhmm
                           , end_hhmm
                           , cntnc_hh
                           , kpx_dr_rsc_id
                           , event_id
                           , event_sttus_cd
                           , uid
)
VALUES (
        NOW()
        , 'admin'
        , #{issueDate}
        , #{issueHhmm}
        , #{endHhmm}
        , #{cntncHh}
        , #{kpxDrRscId}
        , #{eventId}
        , #{eventSttusCd}
        , #{uid}
       )
ON DUPLICATE KEY UPDATE
    reg_dt = NOW()
    , event_sttus_cd = #{eventSttusCd}
