mapper.select:
SELECT a.ami_idx amiIdx, a.mid meterNo, b.custNo custNo FROM tb_ami a, (
SELECT #{item.meterNo} AS meterNo , #{item.custNo} AS custNo
) b WHERE mid IN (
#{item.meterNo}
) AND a.mid = b.meterNo

mapper.insert:
INSERT INTO tb_kepco_api_result(kepco_cstmr_no, ami_idx, data_request_dt, request_success_dt) SELECT * FROM (
( SELECT #{item.custNo} AS kepcoCstmrNo, #{item.amiIdx} AS amiIdx, #{item.meteringDate} AS dataRequestDt, #{item.requestSuccessDt} AS requestSuccessDt )
) AS A ON DUPLICATE KEY UPDATE data_request_dt = IF(dataRequestDt IS NULL, data_request_dt, dataRequestDt), request_success_dt = IF(requestSuccessDt IS NULL, request_success_dt, requestSuccessDt)

mapper.update:
UPDATE tb_kepco_api_result
SET request_success_dt = #{requestSuccessDt}
WHERE kepco_cstmr_no = #{custNo}
AND ami_idx = #{amiIdx}

mapper.insert:
INSERT INTO tb_kepco_hour( metering_date, hh, ami_idx, use_type, reg_dt, mid, kepco_cstmr_no, metering_val, accmlt_metering_val ) SELECT * FROM (
( SELECT DATE_FORMAT(DATE_ADD(DATE_FORMAT(CONCAT( #{item.meteringDate}, #{item.meteringTime},'00'),'%Y%m%d%H%i%s'), INTERVAL -15 MINUTE ),'%Y%m%d'), DATE_FORMAT(DATE_ADD(DATE_FORMAT(CONCAT( #{item.meteringDate}, #{item.meteringTime},'00'),'%Y%m%d%H%i%s'), INTERVAL -15 MINUTE),'%H'), #{item.amiIdx}, '1', sysdate(), #{item.meterNo}, #{item.custNo}, SUM(metering_val) AS meteringVal,
#{item.accmltMeteringVal} AS accmltMeteringVal
MAX(accmlt_metering_val) AS accmltMeteringVal
FROM tb_kepco_15m WHERE 1 = 1 AND use_type = '1' AND mid = #{item.meterNo}
AND (metering_date, hhmm) IN ( ( #{item.meteringDate}, #{item.meteringTime}) ,( DATE_FORMAT(DATE_ADD(DATE_FORMAT(CONCAT( #{item.meteringDate}, '0000','00'),'%Y%m%d%H%i%s'), INTERVAL -15 MINUTE ),'%Y%m%d'), '2345') , (DATE_FORMAT(DATE_ADD(DATE_FORMAT(CONCAT( #{item.meteringDate}, '0000','00'),'%Y%m%d%H%i%s'), INTERVAL -15 MINUTE ),'%Y%m%d'), '2330') , (DATE_FORMAT(DATE_ADD(DATE_FORMAT(CONCAT( #{item.meteringDate}, '0000','00'),'%Y%m%d%H%i%s'), INTERVAL -15 MINUTE ),'%Y%m%d'), '2315') )
AND metering_date = #{item.meteringDate} AND hhmm <= #{item.meteringTime} AND hhmm > CONCAT(DATE_FORMAT(DATE_ADD(DATE_FORMAT(CONCAT( #{item.meteringDate}, #{item.meteringTime},'00'),'%Y%m%d%H%i%s'), INTERVAL -15 MINUTE),'%H'),'00')
)
) AS A
ON DUPLICATE KEY UPDATE metering_val = meteringVal, accmlt_metering_val = accmltMeteringVal

mapper.update:
UPDATE tb_kepco_15m
SET accmlt_metering_val  = (
    SELECT a.accmlt_metering_val + IFNULL(b.metering_val, 0)
    FROM
    (
        SELECT accmlt_metering_val
        FROM tb_kepco_15m
        WHERE ami_idx       = #{amiIdx}
        AND use_type        = '1'
        AND metering_date   = #{meteringDate}
        AND hhmm            = #{previousTime}
    ) AS a, (
        SELECT metering_val
        FROM tb_kepco_15m
        WHERE ami_idx       = #{amiIdx}
        AND use_type        = '1'
        AND metering_date   = #{meteringDate}
        AND hhmm            = #{meteringTime}
    ) b
)
WHERE ami_idx       = #{amiIdx}
  AND use_type      = '1'
  AND metering_date = #{meteringDate}
  AND hhmm          = #{meteringTime}
