mapper.select:
SELECT a.ami_idx amiIdx, a.mid meterNo, b.custNo custNo FROM tb_ami a, (
SELECT #{item.meterNo} AS meterNo , #{item.custNo} AS custNo
) b WHERE mid IN (
#{item.meterNo}
) AND a.mid = b.meterNo

mapper.insert:
INSERT INTO tb_kepco_api_result(kepco_cstmr_no, ami_idx, data_request_dt, request_success_dt) SELECT * FROM (
( SELECT #{item.custNo} AS kepcoCstmrNo, #{item.amiIdx} AS amiIdx, #{item.meteringDate} AS dataRequestDt, #{item.requestSuccessDt} AS requestSuccessDt )
) AS A ON DUPLICATE KEY UPDATE data_request_dt = IF(dataRequestDt IS NULL, data_request_dt, dataRequestDt), request_success_dt = IF(requestSuccessDt IS NULL, request_success_dt, requestSuccessDt)

mapper.update:
UPDATE tb_kepco_api_result
SET request_success_dt = #{requestSuccessDt}
WHERE kepco_cstmr_no = #{custNo}
AND ami_idx = #{amiIdx}

mapper.insert:
INSERT IGNORE INTO tb_kepco_15m( metering_date, hhmm, ami_idx, use_type, reg_dt, mid, kepco_cstmr_no, metering_val, accmlt_metering_val ) VALUES
( #{item.meteringDate}, #{item.meteringTime}, #{item.amiIdx}, '1', sysdate(), #{item.meterNo}, #{item.custNo}, #{item.meteringVal},
#{item.accmltMeteringVal}
IFNULL((SELECT accmlt_metering_val FROM (SELECT MAX(accmlt_metering_val) AS accmlt_metering_val FROM tb_kepco_15m WHERE ami_idx = #{item.amiIdx}) AS a), 0)
)
