mapper.select:
SELECT a.ami_idx amiIdx, a.mid meterNo, b.custNo custNo FROM tb_ami a, (
SELECT #{item.meterNo} AS meterNo , #{item.custNo} AS custNo
) b WHERE mid IN (
#{item.meterNo}
) AND a.mid = b.meterNo

SELECT a.kepco_cstmr_no custNo, a.ami_idx amiIdx, a.data_request_dt dataRequestDt, IF(a.request_success_dt IS NULL, DATE_FORMAT(b.reg_dt,'%Y%m%d'), DATE_FORMAT(DATE_ADD(a.request_success_dt, INTERVAL 1 DAY),'%Y%m%d')) requestSuccessDt FROM tb_kepco_api_result a, tb_cnsmr b WHERE a.kepco_cstmr_no = b.kepco_cstmr_no AND a.del_yn = 'N' AND ( a.request_success_dt != a.data_request_dt OR a.request_success_dt IS NULL ) AND a.kepco_cstmr_no IN (
#{item.custNo}
)

mapper.insert:
INSERT INTO tb_kepco_api_result(kepco_cstmr_no, ami_idx, data_request_dt, request_success_dt) SELECT * FROM (
( SELECT #{item.custNo} AS kepcoCstmrNo, #{item.amiIdx} AS amiIdx, #{item.meteringDate} AS dataRequestDt, #{item.requestSuccessDt} AS requestSuccessDt )
) AS A ON DUPLICATE KEY UPDATE data_request_dt = IF(dataRequestDt IS NULL, data_request_dt, dataRequestDt), request_success_dt = IF(requestSuccessDt IS NULL, request_success_dt, requestSuccessDt)

mapper.update:
UPDATE tb_kepco_api_result
SET request_success_dt = #{requestSuccessDt}
WHERE kepco_cstmr_no = #{custNo}
AND ami_idx = #{amiIdx}

mapper.insert:
INSERT INTO tb_kepco_15m(metering_date, hhmm, mid, use_type, ami_idx, accmlt_metering_val)
SELECT * FROM (
    SELECT  LEFT(date, 8)
          , MID(date, 9, 4)
          , #{meterNo}
          , '1'
          , #{amiIdx}
          , (
            SELECT SUM(metering_val) accmltMeteringVal
            FROM tb_kepco_15m
            WHERE 1        = 1
            AND   use_type = '1'
            AND   ami_idx  = #{amiIdx}
            AND   mid      = #{meterNo}
            AND   CONCAT(metering_date, hhmm)    <=   a.date
            ) accmltMeteringVal
    FROM (
    SELECT CONCAT(metering_date, hhmm) date
    , hhmm
    FROM tb_kepco_15m
    WHERE 1 = 1
    AND   use_type       = '1'
    AND   ami_idx        = #{amiIdx}
    AND   mid            = #{meterNo}
    AND   metering_date  = #{meteringDate}
    ) a
) result
ON DUPLICATE KEY UPDATE
accmlt_metering_val = accmltMeteringVal

mapper.select:
SELECT result.metering_date meteringDate , result.startHhmm startHhmm , MAX(result.hhmm) endHhmm , #{amiIdx} amiIdx FROM ( SELECT @startHhmm := IF(noDifferent != 0, '', IF(@startHhmm != '', @startHhmm, hhmm)) startHhmm, c.* FROM ( SELECT metering_date , a.hhmm hhmm , a.metering_val originalValue , b.metering_val contrastValue , a.metering_val = IFNULL(b.metering_val,'null') noDifferent FROM ( SELECT metering_date , hhmm , IFNULL(metering_val, 'null') metering_val FROM tb_kepco_15m WHERE metering_date = #{meteringDate} AND mid = #{meterNo} AND use_type = '1' ) a LEFT OUTER JOIN (
SELECT #{item.meteringTime} hhmm, CAST(#{item.meteringVal} AS DECIMAL(10,3)) metering_val
) b ON a.hhmm = b.hhmm ) c, (SELECT @startHhmm := '') x ) result WHERE startHHmm != '' GROUP BY startHhmm
